
// Removed node-fetch import as Node.js v18+ has native fetch

async function testIotApi() {
    console.log('Testing IoT API...');

    // 1x1 Pixel Red Dot Base64
    const pixelImage = "data:image/jpeg;base64,";

    try {
        const response = await fetch('http://localhost:9002/api/iot/report', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                deviceId: 'TEST-SCRIPT-001',
                location: { lat: 17.38, lng: 78.48 },
                image: pixelImage
            })
        });

        const data = await response.json();
        console.log('Response Status:', response.status);
        console.log('Response Body:', JSON.stringify(data, null, 2));

        if (response.status === 200 && data.success) {
            console.log('✅ API Test Passed!');
        } else {
            console.log('❌ API Test Failed');
        }

    } catch (err) {
        console.error('Request Failed:', err);
    }
}

testIotApi();
